INSERT INTO `stretchy_report` ( `report_name`, `report_type`, `report_subtype`, `report_category`, `report_sql`, `description`, `core_report`, `use_report`) VALUES ('Client Payments', 'Table', NULL, 'payment', 'select  p.receipt_number ReceiptNo\n,o.name officeName,c.display_name ClientName,c.mobile_no MobileNo\n,sum(ifnull(t.amount,0))TotalLoantrxnAmount\n,sum(ifnull(s.amount,0))TotalSavingstrxnAmount\n,sum(ifnull(t.amount,0)) + sum(ifnull(s.amount,0)) \'total\' \n from  m_payment_detail p\nleft join m_loan_transaction t on t.payment_detail_id = p.id\nleft join m_savings_account_transaction s on s.payment_detail_id = p.id\nleft join m_savings_account sa on sa.id=s.savings_account_id\nleft join m_loan m on m.id = t.loan_id\nleft join m_client c on c.id = m.client_id or c.id=sa.client_id\nleft join m_office o on o.id = c.office_id\nwhere p.receipt_number = \'${reciptNo}\'\n and c.id = \'${clientId}\'\n and s.transaction_type_enum != 7', 'Client payments Details', 0, 1);
